import React from 'react';
import { Carousel } from 'react-responsive-carousel';
import '../style.css'
import ItemsCarousel from 'react-items-carousel';
import range from 'lodash/range';
import {
    BrowserRouter,
    Switch,
    Route,
    Link,
    Redirect,
  } from "react-router-dom";
  import ProductItem from './ProductItem'
import ProductSlider from './ProductSlider';
import placeHolder from './images/golden.jpg'
import ServiceRegisterForm from '../administrador/ServiceRegisterForm';
import ProducSlider from './ProductSlider';


class ProductsScreen extends React.Component {

    fetchProductsFromServer(){

        // fetch fetch fetch

        return [{name: 'Promoções da Semana',
                 items: [
                        {name: 'Test 1', imgSrc: placeHolder, price: 'FF'},
                        {name: 'Test 2', imgSrc: placeHolder, price: 'FF'},
                        {name: 'Test 3', imgSrc: placeHolder, price: 'FF'},
                        {name: 'Test 4', imgSrc: placeHolder, price: 'FF'},
                        {name: 'Test 5', imgSrc: placeHolder, price: 'FF'},
                        {name: 'Test 6', imgSrc: placeHolder, price: 'FF'},
                        {name: 'Test 7', imgSrc: placeHolder, price: 'FF'},
                        {name: 'Test 8', imgSrc: placeHolder, price: 'FF'},
                 ]},
                 {name: 'Rações para Cachorro',
                 items: [
                        {name: 'Test A', imgSrc: placeHolder, price: 'HH'},
                        {name: 'Test B', imgSrc: placeHolder, price: 'HH'},
                        {name: 'Test C', imgSrc: placeHolder, price: 'HH'},
                 ]},
                 {name: 'Rações para Gato',
                 items: [
                        {name: 'Test X', imgSrc: placeHolder, price: 'PP'},
                        {name: 'Test Y', imgSrc: placeHolder, price: 'PP'},
                        {name: 'Test Z', imgSrc: placeHolder, price: 'PP'},
                 ]},
                ]
    }

    createChildren = n => range(n).map(i => <div key={i} style={{ height: 200, background: '#333' }}>{i}</div>);

    componentWillMount() {
      this.setState({
        children: [],
        activeItemIndex: 0,
      });

      setTimeout(() => {
        this.setState({
          children: this.createChildren(20),
        })
      }, 100);

      this.setState({products: this.fetchProductsFromServer()})
    }

    changeActiveItem = (activeItemIndex) => this.setState({ activeItemIndex });

    render() {

        const test = [<ProductItem imgSrc={placeHolder} name='Banana good' price='42'/>,
                      <ProductItem imgSrc={placeHolder} name='Banana good' price='42'/>,
                      <ProductItem imgSrc={placeHolder} name='Banana good' price='42'/>,
                      <ProductItem imgSrc={placeHolder} name='Banana good' price='42'/>,
                      <ProductItem imgSrc={placeHolder} name='Banana good' price='42'/>,
                      <ProductItem imgSrc={placeHolder} name='Banana good' price='42'/>,
                      <ProductItem imgSrc={placeHolder} name='Banana good' price='42'/>,
                      <ProductItem imgSrc={placeHolder} name='Banana good' price='42'/>,
                      <ProductItem imgSrc={placeHolder} name='Banana good' price='42'/>,]
        const receivedContent = this.state.products.map(function(slider){

            let items = slider.items.map(function(item){
                return <ProductItem imgSrc={item.imgSrc} name={item.name} price={item.price}/>
            })
            return <ProducSlider name={slider.name} items={items}/>
        })

        const {
            activeItemIndex,
            children,
          } = this.state;

        return (
            <div>
                <main>
                    {receivedContent}
                    <ItemsCarousel
                        // Placeholder configurations
                        enablePlaceholder
                        numberOfPlaceholderItems={5}
                        minimumPlaceholderTime={1000}
                        placeholderItem={<div style={{ background: '#900' }}>Placeholder</div>}

                        // Carousel configurations
                        numberOfCards={6}
                        gutter={12}
                        showSlither={true}
                        firstAndLastGutter={true}
                        freeScrolling={false}

                        // Active item configurations
                        requestToChangeActive={this.changeActiveItem}
                        activeItemIndex={activeItemIndex}
                        activePosition={'center'}

                        chevronWidth={54}
                        leftChevron={<img src={require("./images/arrow.svg")} class="leftArrow" id="left1"/>}
                        rightChevron={<img src={require("./images/arrow.svg")} class="rightArrow" id="right1"/>}
                        outsideChevron={false}
                    >
                        {test}
                    </ItemsCarousel>
                </main>
            </div>
        );
    }
}

export default ProductsScreen;
