import React from 'react';
import '../style.css'
import {
    BrowserRouter,
    Switch,
    Route,
    Link,
    Redirect,
  } from "react-router-dom";


class PetCard extends React.Component {

    render() {

        return (
            <div class="petCard">
                <img class="image" src={this.props.imgSrc}/>
                <div>
                    {() => {
                        if(this.props.removeMode){
                            return <div>
                                        <h2 style={{textAlign: 'right', paddingLeft: '1em', cursor: 'pointer'}}>
                                            <strong>X</strong>
                                        </h2>
                                    </div>
                        }
                        alert(this.props.removeMode)
                    }}
                    <p class="petName">{this.props.name}</p>
                    <div class="petInfoHolder">
                        <div style={{display: 'flex'}}>
                            <div class="petInfoLabels">
                                <p>ID</p>
                                <p>Ra√ßa</p>
                            </div>
                            <div class="petInfoContent">
                                <p>{this.props.id}</p>
                                <p>{this.props.race}</p>
                            </div>
                        </div>
                        <div style={{display: 'flex'}}>
                            <div class="petInfoLabels">
                                <p>Sexo</p>
                                <p>Idade</p>
                            </div>
                            <div class="petInfoContent">
                                <p>{this.props.sex}</p>
                                <p>{this.props.age + ' anos'}</p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default PetCard;
