import React from 'react';
import '../style.css'
import {
    BrowserRouter,
    Switch,
    Route,
    Link,
    Redirect,
  } from "react-router-dom";


class StockItem extends React.Component {

    render() {
        if(this.props.editMode){
            return (
                <div class="displayCard">
                    <button 
                        type="submit" 
                        onClick={() => this.props.deleteProductHandler(this.props.id)}
                    >
                        Remover
                    </button>
                    <img src={this.props.imgSrc}/>
                    <div class="item">
                        <p class="itemLabel">{this.props.name}</p>
                        <p>Quantidade: 
                            <input 
                                type="number" 
                                placeholder={this.props.quantity}
                                onChange={(event) => this.changeProductHandler(this.props.id, event.target.value)}
                            >
                            </input>
                            </p>
                    </div>
                </div>
            );
        }

        return (
            <div class="displayCard">
                <img src={this.props.imgSrc}/>
                <div class="item">
                    <p class="itemLabel">{this.props.name}</p>
                    <p>Quantidade: {this.props.quantity} uni.</p>
                </div>
            </div>
        );
    }
}

export default StockItem;
