import React from 'react';
import '../style.css'
import {
    BrowserRouter,
    Switch,
    Route,
    Link,
    Redirect,
  } from "react-router-dom";
import CartItem from './CartItem';


class ShopCartPanel extends React.Component {

    buttonHandler = () =>{
        this.props.parent = {id: 25}
    }

    render() {
        return (
            <main style={{display: "flex", justifyContent: "center", alignItems: "center"}}>
                <div id="boxCart">
                    <h1>Meu carrinho</h1>
                    <div style={{display: "flex", justifyContent: "center", alignItems: "center"}}>
                        <div style={{padding:2 + 'em'}}>{this.props.items}</div>
                        <div id="totalCart">
                            <h3>Total : R$ {Number(this.props.totalCost).toFixed(2)}</h3>
                            <button> Comprar </button>
                        </div>
                    </div>
                </div>
                <button onClick={this.buttonHandler}>Change</button>
            </main>
        );
    }
}

export default ShopCartPanel;
