import React from 'react';
import '../style.css'
import {
    BrowserRouter,
    Switch,
    Route,
    Link,
    Redirect,
  } from "react-router-dom";
  import ProfileCard from './ProfileCard'
  import OwnedPetsPanel from './OwnedPetsPanel'
  import profilePlaceHolder from './images/coffinGuy.jpg'
  import placeHolder from './images/golden.jpg'
import PetCard from './PetCard';
import ShopCartPanel from './ShopCartPanel';
import CartItem from './CartItem'



class ShowerBookingScreen extends React.Component {

    render() {

        const shops = [<CartItem imgSrc={placeHolder} name="Ração do Cão"  quantity='1' price="63,00"/>,
                      <CartItem imgSrc={placeHolder} name="Ração do Gato" quantity='2' price="73,00"/>,
                      <CartItem imgSrc={placeHolder} name="Ração do Rato" quantity='3' price="83,00"/>,]

        return (
            <main>
                <div class="formAgendarHolder">
                    <div class="formAgendar">
                        <h1>Banho/Tosa</h1>
                        <input type="text" placeholder="Nome e sobrenome" class="nameInput"/>
                        <input type="text" placeholder="Nome do animal" class="nameInput"/>
                        <div style={{width: '100%', textAlign: 'left'}}>
                            <input type="date" class="timeInput"/>
                            <input type="time" class="timeInput"/>
                        </div>
                        <div style={{width: '100%', textAlign: 'left'}}>
                            <select>
                                <option>Serviço</option>
                                <option>Só banho</option>
                                <option>Só tosa</option>
                                <option>Banho e tosa</option>
                            </select>
                        </div><br/>
                        <h3>Custo: R$ 20,00</h3>
                        <button type="submit">Agendar</button>
                    </div>
                </div>
            </main>
        );
    }
}

export default ShowerBookingScreen;
