import React from 'react';
import '../style.css'
import {
    BrowserRouter,
    Switch,
    Route,
    Link,
    Redirect,
  } from "react-router-dom";
  import ProductItem from './ProductItem'
import ProductSlider from './ProductSlider';
import placeHolder from './images/golden.jpg'


class ProductsScreen extends React.Component {

    render() {

        const prod1 = [<ProductItem imgSrc={placeHolder} name="Test1" price="CC"/>,
                       <ProductItem imgSrc={placeHolder} name="Test2" price="CC"/>,
                       <ProductItem imgSrc={placeHolder} name="Test3" price="CC"/>,
                       <ProductItem imgSrc={placeHolder} name="Test4" price="CC"/>,
                       <ProductItem imgSrc={placeHolder} name="Test5" price="CC"/>,]

        const prod2 = [<ProductItem imgSrc={placeHolder} name="Test1" price="CC"/>,
                       <ProductItem imgSrc={placeHolder} name="Test2" price="CC"/>,
                       <ProductItem imgSrc={placeHolder} name="Test3" price="CC"/>,]

        return (
            <div>
                <main>
                    <ProductSlider name="Promoções da semana" items={prod1}/>

                    <div class="productSliderHolder">
                        <h1>Ração para gatos</h1>
                        <div class="productSlider">
                            <ProductItem imgSrc="./images/golden.jpg" name="Test" price="CC"/>
                        </div>
                    </div>
                    <div class="productSliderHolder">
                        <h1>Ração para cachorros</h1>
                        <div class="productSlider">
                            <ProductItem imgSrc="./images/golden.jpg" name="Test" price="CC"/>
                        </div>
                    </div>
                </main>
            </div>
        );
    }
}

export default ProductsScreen;
