import React from 'react';
import '../style.css'
import {
    BrowserRouter,
    Switch,
    Route,
    Link,
    Redirect,
  } from "react-router-dom";
  import ProfileCard from './ProfileCard'
  import OwnedPetsPanel from './OwnedPetsPanel'
  import profilePlaceHolder from './images/coffinGuy.jpg'
  import placeHolder from './images/golden.jpg'
import PetCard from './PetCard';
import ShopCartPanel from './ShopCartPanel';
import CartItem from './CartItem'



class ShopCartScreen extends React.Component {

    fetchItemsFromServer(userId){

        //fetch fetch fetch

        return [{id: 120, imgSrc: placeHolder, name: "Ração do Cão" , quantity: '1', price: "63.00"},
                {id: 121, imgSrc: placeHolder, name: "Ração do Gato", quantity: '2', price: "73.00"},
                {id: 122, imgSrc: placeHolder, name: "Ração do Rato", quantity: '3', price: "83.00"},]

    }
    // componentWillMount(){

    //     this.setState({shops: this.fetchItemsFromServer(1234).map((item) => {

    //                     totalCost += item.quantity*item.price
    //                     return <CartItem imgSrc={placeHolder} name={item.name} quantity={item.quantity} price={item.price}/>
    //                 })})
    // }

    render() {

        let totalCost = 0

        let shops = this.fetchItemsFromServer(1234).map((item) => {

            totalCost += item.quantity*item.price

            return <CartItem imgSrc={placeHolder} name={item.name} quantity={item.quantity} price={item.price}/>
        })

        return (
            <ShopCartPanel items={shops} totalCost={totalCost} parent={this.state}/>
        );
    }
}

export default ShopCartScreen;
