import React from 'react';
import '../style.css'
import {
    BrowserRouter,
    Switch,
    Route,
    Link,
    Redirect,
  } from "react-router-dom";
  import StockItem from './StockItem'


class StockPanel extends React.Component {

    componentWillMount(){
        this.setState({ racaoGato: true,
                        racaoCao: true,
                        areiaGato: true,
                        petiscos: true
                      })
    }

    racaoGatoHandler = () => {
        this.setState({racaoGato: !this.state.racaoGato})
    }
    racaoCaoHandler = () => {
        this.setState({racaoCao: !this.state.racaoCao})
    }
    areiaGatoHandler = () => {
        this.setState({areiaGato: !this.state.areiaGato})
    }
    petiscosHandler = () => {
        this.setState({petiscos: !this.state.petiscos})
    }

    render() {

        const items = this.props.itemsData.map((item) => {

            if(item.type == 1 && this.state.racaoGato)
                return <StockItem imgSrc={item.imgSrc} name={item.name} quantity={item.quantity} id={item.id}/>
            if(item.type == 2 && this.state.racaoCao)
                return <StockItem imgSrc={item.imgSrc} name={item.name} quantity={item.quantity} id={item.id}/>
            if(item.type == 3 && this.state.areiaGato)
                return <StockItem imgSrc={item.imgSrc} name={item.name} quantity={item.quantity} id={item.id}/>
            if(item.type == 4 && this.state.petiscos)
                return <StockItem imgSrc={item.imgSrc} name={item.name} quantity={item.quantity} id={item.id}/>

        })

        return (
            <main>
                <div id="displayPanelHolder">
                    <h2>
                        Estoques
                        <span class="estoque">        
                        <button type="submit">Editar</button>
                        <button type="submit">Remover</button>
                        <button type="submit">Adicionar</button>
                        </span>
                    </h2>

                    <span id="filtros">
                        <p>Filtrar:</p>
                        <input type="checkbox" id="gatos" name="Gatos" checked />
                        <label>Ração de Gato</label>
                        <input type="checkbox" id="cachorros" name="Cachorros" checked />
                        <label>Ração de cachorro</label>
                        <input type="checkbox" id="adocao" name="Adocao" checked />
                        <label>Areia de gato</label>
                        <input type="checkbox" id="venda" name="Venda" checked />
                        <label>Petiscos</label>
                    </span>

                    <div class="displayPanel">
                        {items}
                    </div>
                </div>
            </main>
        );
    }
}

export default StockPanel;
