import React from 'react';
import '../style.css'



class ProductRegisterForm extends React.Component {

    componentWillMount(){
        this.setState({ name: '',
                        id: '',
                        description: '', // --> description
                        price: '', // --> price
                        quantity: 0,           // --> quantity
                        photo: null,
                      })
    }

    nameHandler = (event) => {
        this.setState({name: event.target.value})
    }
    idHandler = (event) => {
        this.setState({id: event.target.value})
    }
    emailHandler = (event) => {
        this.setState({email: event.target.value})
    }
    phoneHandler = (event) => {
        this.setState({phone: event.target.value})
    }
    photoHandler = (event) => {
        this.setState({photo: event.target.value})
    }
    submitHandler = () => {
        //send to server
        alert("Produto registrado")
    }

    render() {

        return (
            <main>
                <div class="formAgendarHolder">
                    <div class="formAgendar  shadow">
                        <h1>Novo Cliente </h1>
                        <input 
                            type="text" 
                            placeholder="Nome do produto" 
                            class="nameInput"
                            value={this.state.name}
                            onChange={this.nameHandler}
                        />
                        <input 
                            type="text" 
                            placeholder="ID" 
                            class="nameInput"
                            value={this.state.id}
                            onChange={this.idHandler}
                        />
                        <input 
                            type="text" 
                            placeholder="Descrição" 
                            class="nameInput"
                            value={this.state.description}
                            onChange={this.descriptionHandler}
                        />
                        <input 
                            type="number" 
                            placeholder="Preço" 
                            class="nameInput"
                            value={this.state.price}
                            onChange={this.priceHandler}
                        />
                        <input 
                            type="number" 
                            placeholder="Quantidade" 
                            class="nameInput"
                            value={this.state.quantity}
                            onChange={this.quantityHandler}
                        />
                        <input 
                            type="file" 
                            class="fileInput"
                            value={this.state.photo}
                            onChange={this.photoHandler}
                        />

                        <button type="submit" onClick={this.submitHandler}>Confirmar</button>
                    </div>
                </div>    
            </main>
        );
    }
}

export default ProductRegisterForm;
