import React from 'react';
import '../style.css'
import {
    Link,
    Redirect,
  } from "react-router-dom";


class ClientLoginForm extends React.Component {

    componentWillMount(){
        this.setState({ cpf: '',
                        password: '',
                        logedin: false,
                      })
    }

    cpfHandler = (event) => {
        this.setState({cpf: event.target.value})
    }
    passwordHandler = (event) => {
        this.setState({password: event.target.value})
    }
    submitHandler = () => {

        //client authentication

       this.setState({logedin: true})
    }

    render() {

        if(!this.state.logedin){
            return (
                <main>
                    <div class="formAgendarHolder">
                        <div class="formAgendar  shadow">
                            <h1>Login Cliente </h1>
                            <input 
                                type="text" 
                                placeholder="CPF" 
                                class="nameInput"
                                value={this.state.cpf}
                                onChange={this.cpfHandler}
                            />
                            <input 
                                type="password" 
                                placeholder="Senha" 
                                class="nameInput"
                                value={this.state.password}
                                onChange={this.passwordHandler}
                            />

                            <button type="submit" onClick={this.submitHandler}>Confirmar</button>


                        </div>
                    </div>

                    <div class="formAgendarHolder">
                        <div class="formAgendar  shadow">
                            <button type="submit" onClick={this.submitHandler}>Novo cliente</button>
                            <button type="submit" onClick={this.submitHandler}>Logar como Admin</button>
                        </div>
                    </div>

                </main>
            );
        }
        else
            return(
                <Redirect to="/client/produtos"/>
            )
    }
}

export default ClientLoginForm;
