import React from 'react';
import '../style.css'
import {
    BrowserRouter,
    Switch,
    Route,
    Link,
    Redirect,
  } from "react-router-dom";
  import ProfileCard from './ProfileCard'
  import OwnedPetsPanel from './OwnedPetsPanel'
  import profilePlaceHolder from './images/coffinGuy.jpg'
  import placeHolder from './images/golden.jpg'
import PetCard from './PetCard';
import ShopCartPanel from './ShopCartPanel';
import CartItem from './CartItem'



class ShopCartScreen extends React.Component {

    fetchItemsFromServer(userId){

        //fetch fetch fetch

        return [{id: 120, imgSrc: placeHolder, name: "Ração do Cão" , quantity: '1', price: "63.00"},
                {id: 121, imgSrc: placeHolder, name: "Ração do Gato", quantity: '2', price: "73.00"},
                {id: 122, imgSrc: placeHolder, name: "Ração do Rato", quantity: '3', price: "83.00"},]

    }

    componentWillMount(){
        this.setState({itemsData: this.fetchItemsFromServer(1234)})
    }

    componentDidMount(){

        let tempCost = 0
        for(let i in this.state.itemsData)
            tempCost += i.quantity*i.price

        this.setState({totalCost: tempCost})
    }

    changeQuantityHandler = (productId, quantity) => {

        let tempArr = this.state.itemsData

        for(let i in tempArr)
            if(i.id == productId)
                i.quantity = quantity

        let tempCost = 0
        for(let i in tempArr)
            tempCost += i.quantity*i.price

        this.setState({itemsData: tempArr, totalCost: tempCost})

        // send new purchase information to server
    }

    render() {

        let shops = this.state.itemsData.map((item) => {

            return <CartItem
                    id={item.id}
                    imgSrc={placeHolder} 
                    name={item.name} 
                    quantity={item.quantity} 
                    price={item.price}
                    changeQuantityHandler={this.changeQuantityHandler}
                    />
        })

        return (
            <ShopCartPanel items={shops} totalCost={this.state.totalCost}/>
        );
    }
}

export default ShopCartScreen;
