import React from 'react';
import '../style.css'
import {
    BrowserRouter,
    Switch,
    Route,
    Link,
    Redirect,
  } from "react-router-dom";
  import AdoptionPanel from './AdoptionPanel'
  import AdoptionItem from './AdoptionItem'
  import dogPlaceHolder from './images/dog.jpg'
  import catPlaceHolder from './images/gato_site.png'



class AdoptionScreen extends React.Component {

    componentWillMount(){
        this.setState({
            dog: true,
            cat: true,
            adopt: true,
            sell: true,
        })
    }

    toggleCat = () => { this.setState({cat: !this.state.cat}) }
    toggleDog = () => { this.setState({dog: !this.state.dog}) }
    toggleAdopt = () => { this.setState({adopt: !this.state.adopt}) }
    toggleSell = () => { this.setState({sell: !this.state.sell}) }

    render() {

        const pets = [ <AdoptionItem imgSrc={dogPlaceHolder} species="dog" type="sell" name="Dog" race="Beagle" age="6"/>,
                       <AdoptionItem imgSrc={catPlaceHolder} species="cat" type="adopt" name="Cat" race="Persa" age="4"/>,
                       <AdoptionItem imgSrc={catPlaceHolder} species="cat" type="adopt" name="Cat" race="Persa" age="4"/>,
                       <AdoptionItem imgSrc={catPlaceHolder} species="cat" type="sell" name="Cat" race="Persa" age="4"/>,
                       <AdoptionItem imgSrc={dogPlaceHolder} species="dog" type="sell" name="Dog" race="Beagle" age="6"/>,
                       <AdoptionItem imgSrc={dogPlaceHolder} species="dog" type="sell" name="Dog" race="Beagle" age="6"/>,
                       <AdoptionItem imgSrc={dogPlaceHolder} species="dog" type="adopt" name="Dog" race="Beagle" age="6"/>,
                       <AdoptionItem imgSrc={catPlaceHolder} species="cat" type="adopt" name="Cat" race="Persa" age="4"/>,]

        return (
            <div id="displayPanelHolder">
                <h2>Nossos animais estão buscando um lar!</h2>
                <div id="filtros">
                    <p>Filtrar:</p>
                    <input type="checkbox" id="gatos" name="Gatos" checked onChange={this.toggleCat}/>
                    <label>Gatos</label>
                    <input type="checkbox" id="cachorros" name="Cachorros" checked onChange={this.toggleDog}/>
                    <label>Cachorros</label>
                    <input type="checkbox" id="adocao" name="Adocao" checked onChange={this.toggleAdopt}/>
                    <label>Adoção</label>
                    <input type="checkbox" id="venda" name="Venda" checked onChange={this.toggleSell}/>
                    <label>Venda</label>
                </div>
                <AdoptionPanel items={pets}/>
                <p>{this.state.dog}</p>
                <p>{this.state.cat}</p>
                <p>{this.state.adopt}</p>
                <p>{this.state.sell}</p>
            </div>
        );
    }
}

export default AdoptionScreen;
