import React from 'react';
import '../style.css'
import {
    BrowserRouter,
    Switch,
    Route,
    Link,
    Redirect,
  } from "react-router-dom";
  import ProfileCard from './ProfileCard'
  import OwnedPetsPanel from './OwnedPetsPanel'
  import profilePlaceHolder from './images/coffinGuy.jpg'
  import petPlaceHolder from './images/dog2.jpeg'
import PetCard from './PetCard';



class AdoptionScreen extends React.Component {

    fetchProfileInfo(){

        //fetch fetch fetch

        return {
                imgSrc: profilePlaceHolder,
                name: 'Laura Genari Alves de Jesus',
                address: 'Rua dos Bobos, 0',
                phone: '16 99999-8888',
                email: 'lau.genari@gmail.com',
                cpf: '439.973.222-98',
                }
    }

    fetchPetsInfo(){

        //fetch fetch fetch

        return [{imgSrc: petPlaceHolder, name: "Gilberto", race: "Beagle", age: "6", id: 'xxxyyz',},
                {imgSrc: petPlaceHolder, name: "Antonio", race: "Beagle", age: "6", id: 'xxxyyz',},
                {imgSrc: petPlaceHolder, name: "Mario", race: "Beagle", age: "6", id: 'xxxyyz',},
                {imgSrc: petPlaceHolder, name: "Antonio", race: "Beagle", age: "6", id: 'xxxyyz',},
                {imgSrc: petPlaceHolder, name: "Gustavo", race: "Beagle", age: "6", id: 'xxxyyz',},
                {imgSrc: petPlaceHolder, name: "Gabriel", race: "Beagle", age: "6", id: 'xxxyyz',},
                {imgSrc: petPlaceHolder, name: "Leandro", race: "Beagle", age: "6", id: 'xxxyyz',},
                {imgSrc: petPlaceHolder, name: "José", race: "Beagle", age: "6", id: 'xxxyyz',},]

    }

    componentWillMount(){

    }


    render() {

        const pets = this.fetchPetsInfo().map((pet) => {

            return <PetCard imgSrc={pet.imgSrc} name={pet.name} race={pet.race} age={pet.age} id={pet.id}/>
        })

        const profileInfo = this.fetchProfileInfo()

        return (
            <main id="profileHolder">
                <div class="perfilHolder">
                    <ProfileCard
                        imgSrc={profileInfo.imgSrc}
                        id={profileInfo.id}
                        name={profileInfo.name}
                        address={profileInfo.address}
                        phone={profileInfo.phone}
                        email={profileInfo.email}
                        cpf={profileInfo.cpf}
                    />
                    <OwnedPetsPanel items={pets}/>
                </div>
            </main>
        );
    }
}

export default AdoptionScreen;
